    <script>
        window.onload = () => {
            const pop = new popinfo;
            const urlParams = new URLSearchParams(window.location.search)
            const loginError = urlParams.get('login_error')
            let errorDetail = getCookie("login_error")
            errorDetail = errorDetail ? decodeURIComponent(errorDetail) : null

            // Remove error cookie
            document.cookie = `login_error=;expires=Thu, 01 Jan 1970 00:00:00 UTC;path=/`

            if (loginError === '1' && errorDetail) {
                pop.error(`Login failed: ${errorDetail}`)
            } else if (loginError === '0') {
                location.href = binus.home_url + "/dashboard/"
            }
        }

        function getCookie(name) {
            const nameEQ = name + "=";
            const ca = document.cookie.split(';');
            for (let i = 0; i < ca.length; i++) {
                let c = ca[i];
                while (c.charAt(0) === ' ') c = c.substring(1, c.length);
                if (c.indexOf(nameEQ) === 0) return c.substring(nameEQ.length, c.length);
            }
            return null;
        }

        function doLogin() {
            window.location.href = `${binus.microsoft_login_uri}&nonce=ce646c335e`
        }

        document.getElementById("defaultOpen").click();
        document.getElementById("tabnew").style.display = "none";
        setTimeout(hide, 1000);

        function hide() {

            document.getElementById("tabnew").style.display = "flex";
        }

        function OpenTab(evnt, tabName) {
            var i, tabcontent, tablinks;
            tabcontent = document.getElementsByClassName("tabcontent");
            for (i = 0; i < tabcontent.length; i++) {
                tabcontent[i].style.display = "none";
            }
            tablinks = document.getElementsByClassName("tablinks");
            for (i = 0; i < tablinks.length; i++) {
                tablinks[i].className = tablinks[i].className.replace(" active", "");
            }
            document.getElementById(tabName).style.display = "block";
            evnt.currentTarget.className += " active";
        }
    </script>
